package Day1;
import java.util.Scanner;
public class Num {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc=new Scanner(System.in);
		int a;
		a=sc.nextInt();
		if(a>0) {
			System.out.println("A is positive");
		}else {
			System.out.println("A is negative");
		}

	}

}
